mapper.insert:
INSERT INTO account(
	USERNAME,
	PASSWORD
)
VALUES(
	#{USERNAME},
	#{PASSWORD}
)


	SELECT LAST_INSERT_ID() as returnedId

mapper.update:
UPDATE account
SET 
	USERNAME = #{USERNAME},
	PASSWORD = #{PASSWORD}
WHERE ID = #{ID_ACCOUNT}

UPDATE account
SET 
	USERNAME = #{USERNAME}
WHERE ID = #{ID_ACCOUNT}
